// Input: map your Airtable "Appraisal Date" to appraisalDate
const { appraisalDate } = input;

// Quick validation
if (!appraisalDate) {
  return { daysDifference: null, normalizedDate: null };
}

const appraisal = new Date(appraisalDate);
if (isNaN(appraisal)) {
  return { daysDifference: null, normalizedDate: null };
}

// Convert both to UTC midnight to avoid timezone issues
const today = new Date();
const todayUTC = Date.UTC(today.getFullYear(), today.getMonth(), today.getDate());
const appraisalUTC = Date.UTC(appraisal.getFullYear(), appraisal.getMonth(), appraisal.getDate());

// Calculate difference (in days)
const diffMs = appraisalUTC - todayUTC;
const daysDifference = diffMs / (1000 * 60 * 60 * 24);

// Normalize date for clarity
const normalizedDate = appraisal.toISOString().split("T")[0];

// Return with debug info
return { 
  daysDifference: daysDifference,
  normalizedDate: normalizedDate,
  debugAppraisalDate: appraisal.toISOString(),
  debugTodayDate: today.toISOString(),
  debugDiffMs: diffMs
};

// The JSON is coming in as a long-text string from Airtable.
const data = JSON.parse(input.appraisalHistory);

// Get current datetime
const nowIso = new Date().toISOString();
const currentYear = new Date().getFullYear();

// Ensure structure exists
if (!data || !Array.isArray(data.appraisals)) {
  return { error: "Invalid data structure." };
}

// Update the relevant appraisal
data.appraisals = data.appraisals.map(appraisal => {
  if (appraisal.year !== currentYear) return appraisal;

  appraisal.steps = appraisal.steps.map(step => {
    if (step.id === "sent_pre_appraisal_form") {
      return { ...step, completedAt: nowIso };
    }
    return step;
  });

  appraisal.updatedAt = nowIso;
  return appraisal;
});

// Return JSON so you can store it back in Airtable
return {
  updatedJson: JSON.stringify(data)
};
